INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2015-05-03 01:46:09', '2015-05-03 01:46:09', 'Bienvenido(a) a WordPress. Esta es su primera entrada. Edítela o bórrela ¡y empiece a publicar! Gracias por usar <a href="http://pe.wordpress.org/">WordPress en español</a>.', '¡Hola mundo!', '', 'publish', 'open', 'open', '', 'hola-mundo', '', '', '2015-05-03 01:46:09', '2015-05-03 01:46:09', '', 0, 'http://local.ecommerce.cl/?p=1', 0, 'post', '', 1),
(2, 1, '2015-05-03 01:46:09', '2015-05-03 01:46:09', 'Esta es una página de ejemplo. Es diferente a una entrada del blog, ya que se quedará en un lugar y se mostrará en la navegación del sitio (en la mayoría de temas). La mayoría de personas empieza con una página "Acerca de" que brinda información a los visitantes. Se podría decir algo como esto:\n\n<blockquote>¡Hola! Durante el día soy un mensajero, un aspirante a actor por la noche, y este es mi blog. Vivo en Lima, tengo un enorme perro llamado Pocho, y me gusta el Pisco Sour. (Y caminar bajo la lluvia.)</blockquote>\n\n...o algo como esto:\n\n<blockquote>La compañía XYZ, se fundó en 1971, y ha estado desde entonces, proporcionando artilugios de calidad al público. Está situado en la ciudad de Lima, XYZ emplea a más de 2,000 personas y hace todo tipo de cosas sorprendentes para la comunidad limeña.</blockquote>\n\nComo nuevo usuario de WordPress, usted debe ir a <a href="http://local.ecommerce.cl/wp-admin/">su panel</a> para eliminar esta página y crear nuevas para su contenido. ¡Que se divierta!', 'Página de ejemplo', '', 'publish', 'open', 'open', '', 'pagina-de-ejemplo', '', '', '2015-05-03 01:46:09', '2015-05-03 01:46:09', '', 0, 'http://local.ecommerce.cl/?page_id=2', 0, 'page', '', 0),
(4, 1, '2015-05-03 02:43:02', '2015-05-03 02:43:02', '', 'Shop', '', 'publish', 'closed', 'open', '', 'shop', '', '', '2015-05-03 02:43:02', '2015-05-03 02:43:02', '', 0, 'http://local.ecommerce.cl/?page_id=4', 0, 'page', '', 0),
